bootstrap_transmission <- function(results, N_samples, uncorrelate = T, burnin = 0.1, plot_ci = T){
  if(uncorrelate){
    if(is.null(results[[1]]$uncorrelated)){
      results <- uncorrelate(results, burnin = burnin)
    }
  }else{
    if(is.null(results[[1]]$uncorrelated)){
      warning("It is highly recommended to use uncorrelate = TRUE when calling bootstrap_transmission() on correlated MCMC samples.")
    }
  }

  N <- results[[1]]$N
  out <- matrix(nrow = 0, ncol = N)
  for (i in 1:N_samples) {
    indices <- sample(1:length(results), length(results), replace = T)
    boot <- results[indices]
    ancestry <- rep(0, N)
    for (j in 1:length(results)) {
      ancestry <- ancestry + sapply(1:N, function(x){sum(boot[[j]]$anc == x, na.rm = T)})
    }
    ancestry <- ancestry / length(results)
    out <- rbind(out, ancestry)
  }

  if(plot_ci){
    p_data <- reshape2::melt(out)[,2:3]
    p_data$Var2 <- paste(p_data$Var2)
    p_data$Var2[p_data$Var2 == "1"] <- "Index"
    colnames(p_data)[1] <- "Host"
    p_data$Host <- factor(p_data$Host, levels = c("Index", paste(2:N)))

    print(ggplot2::ggplot(p_data, ggplot2::aes(x = Host, y = value)) +
      ggplot2::geom_boxplot(outlier.size=-1, color = "#2255BB", fill = "#AACCEE") +
      ggplot2::ylab("Number of Transmissions") +
      ggplot2::theme_minimal())
  }

  colnames(out) <- c("Index", paste(2:N))
  rownames(out) <- NULL
  return(out)
}









