# SOFTWARE.

uncorrelate <- function(results, burnin = 0.1){
  # Delete burnin period
  results <- results[1:length(results) > burnin*length(results)]

  # Log-posterior density
  tr <- c()
  for (i in 1:length(results)) {
    tr <- c(tr, sum(results[[i]]$g_lik) + results[[i]]$e_lik + sum(results[[i]]$u_lik))
  }

  # IAT (rounded up)
  capture.output(iat <- ceiling(LaplacesDemon::IAT(tr)))
  results <- results[seq(1, length(results), iat)]

  # Make a note that we've uncorrelated the samples
  results[[1]]$uncorrelated <- T

  return(results)
}
